grand_avg_LFP_Go_Eu = mean(avg_LFP_sess_Go(:,:,ismember(SessNumb, Sess_EuP1)), ...
    3, 'omitnan');

avg_LFP_sess_NC = structfun(@(x) x.avg_LFP_NC, avg_LFP, ...
    'UniformOutput',false);
