avg_LFP_sess_NC = cat(3, avg_LFP_sess_NC{:});
grand_avg_LFP_NC_Eu = mean(avg_LFP_sess_NC(:,:,ismember(SessNumb, Sess_EuP1)), ...
    3, 'omitnan');

